import React, { useState, useEffect } from "react";
import "./AddGoalModal.css";

const AddGoalModal = ({ onClose, onAddGoal }) => {
  const [newGoal, setNewGoal] = useState({
    title: "",
    description: "",
    imgSrc: "",
    actions: [],
    completed: false
  });

  const [actionInput, setActionInput] = useState("");

  // Завантаження існуючих цілей з localStorage при ініціалізації
  useEffect(() => {
    const savedGoals = JSON.parse(localStorage.getItem("goalsData")) || [];
    setNewGoal({ ...newGoal, actions: savedGoals });
  }, []);

  const addAction = () => {
    if (actionInput.trim()) {
      setNewGoal((prev) => ({
        ...prev,
        actions: [...prev.actions, actionInput.trim()]
      }));
      setActionInput("");
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const localUrl = URL.createObjectURL(file);
      setNewGoal((prev) => ({ ...prev, imgSrc: localUrl }));
    }
  };

  const handleSubmit = () => {
    if (newGoal.title && newGoal.description && newGoal.imgSrc) {
      const goalWithId = {
        ...newGoal,
        id: Date.now()
      };

      // Збереження нової цілі в localStorage
      const storedGoals = JSON.parse(localStorage.getItem("goalsData")) || [];
      storedGoals.push(goalWithId);

      localStorage.setItem("goalsData", JSON.stringify(storedGoals));

      // Збереження деталі цілі
      const goalDetails = {
        id: goalWithId.id,
        imgSrc: goalWithId.imgSrc,
        title: goalWithId.title,
        description: goalWithId.description,
        actions: goalWithId.actions
      };

      const storedDetails = JSON.parse(localStorage.getItem("jsonDetails")) || [];
      storedDetails.push(goalDetails);

      localStorage.setItem("jsonDetails", JSON.stringify(storedDetails));

      onAddGoal(goalWithId);
      onClose();
    } else {
      alert("Заповніть всі поля перед збереженням.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>Нова ціль</h2>
        <input
          type="text"
          placeholder="Назва"
          value={newGoal.title}
          onChange={(e) => setNewGoal({ ...newGoal, title: e.target.value })}
        />
        <textarea
          placeholder="Опис"
          value={newGoal.description}
          onChange={(e) => setNewGoal({ ...newGoal, description: e.target.value })}
        ></textarea>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {newGoal.imgSrc && (
          <img src={newGoal.imgSrc} alt="preview" className="preview-image" />
        )}
        <div className="actions-section">
          <input
            type="text"
            placeholder="Нова дія"
            value={actionInput}
            onChange={(e) => setActionInput(e.target.value)}
          />
          <button onClick={addAction}>Додати дію</button>
          <ul>
            {newGoal.actions.map((action, index) => (
              <li key={index}>✔ {action}</li>
            ))}
          </ul>
        </div>
        <div className="modal-buttons">
          <button onClick={handleSubmit}>Зберегти</button>
          <button className="cancel" onClick={onClose}>Скасувати</button>
        </div>
      </div>
    </div>
  );
};

export default AddGoalModal;
