import React, { useState } from "react";
import "./AddGoalModal.css";

const AddGoalModal = ({ onClose, addNewGoal }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [imgSrc, setImgSrc] = useState("");
  const [actions, setActions] = useState([""]);

  const handleAddAction = () => {
    setActions([...actions, ""]);
  };

  const handleChangeAction = (index, value) => {
    const updatedActions = [...actions];
    updatedActions[index] = value;
    setActions(updatedActions);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newGoal = {
      id: Date.now(),
      title,
      description,
      imgSrc,
      actions,
      completed: false,
    };

    addNewGoal(newGoal);
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-btn" onClick={onClose}>✖</button>
        <h2>Додати нову ціль</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Назва цілі"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <textarea
            placeholder="Опис цілі"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />

          <input
            type="text"
            placeholder="URL зображення"
            value={imgSrc}
            onChange={(e) => setImgSrc(e.target.value)}
            required
          />

          <h4>Actions:</h4>
          {actions.map((action, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Action ${index + 1}`}
              value={action}
              onChange={(e) => handleChangeAction(index, e.target.value)}
            />
          ))}

          <button type="button" onClick={handleAddAction}>
            + Додати action
          </button>

          <button type="submit">Додати ціль</button>
        </form>
      </div>
    </div>
  );
};

export default AddGoalModal;
