// File: src/components/Goals/Goals.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../../firebase/firebase";
import Progress from "../Progress/Progress";
import AddGoalModal from "../AddGoalModal/AddGoalModal";
import "./Goals.css";
import {
  createGoal,
  getUserGoals,
  updateGoal as updateGoalDB,
  deleteGoal as deleteGoalDB
} from "../../firebase/filestore";

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [filter, setFilter] = useState("all");
  const [showModal, setShowModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  const fetchGoals = async (uid) => {
    const userGoals = await getUserGoals(uid);
    setGoals(userGoals);
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setUserId(user.uid);
        fetchGoals(user.uid);
      } else {
        setIsAuthenticated(false);
        setUserId(null);
        setGoals([]);
      }
    });
    return () => unsubscribe();
  }, []);

  const toggleGoal = async (index) => {
    const goal = goals[index];
    await updateGoalDB(goal.id, { completed: !goal.completed });
    fetchGoals(userId);
  };

  const deleteGoal = async (index) => {
    const goal = goals[index];
    await deleteGoalDB(goal.id);
    fetchGoals(userId);
  };

  const addGoal = async (newGoal) => {
    await createGoal({
      ...newGoal,
      userId,
      completed: false
    });
    fetchGoals(userId);
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all"
      ? true
      : filter === "completed"
      ? goal.completed
      : !goal.completed
  );

  const completedGoals = goals.filter((goal) => goal.completed).length;
  const totalGoals = goals.length;

  return (
    <section id="goals">
      {isAuthenticated ? (
        <>
          <div className="filter-container">
            <select onChange={(e) => setFilter(e.target.value)} value={filter}>
              <option value="all">Всі</option>
              <option value="active">Активні</option>
              <option value="completed">Виконані</option>
            </select>
          </div>

          <div className="goal-cards">
            {filteredGoals.map((goal, index) => (
              <article
                className={`goal-card ${goal.completed ? "completed" : ""}`}
                key={goal.id}
                onClick={() => (window.location.href = `/goal/${goal.id}`)}
              >
                <img src={goal.imgSrc} alt={goal.title} />
                <h3>{goal.title}</h3>
                <button
                  className="mark-done"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleGoal(index);
                  }}
                >
                  {goal.completed ? "Виконано" : "Виконати"}
                </button>
                <button
                  className="delete-goal"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteGoal(index);
                  }}
                >
                  ✖
                </button>
              </article>
            ))}

            <article className="goal-card add-goal" onClick={() => setShowModal(true)}>
              <div className="plus-sign">+</div>
              <h3>Додати ціль</h3>
            </article>
          </div>

          <Progress completedGoals={completedGoals} totalGoals={totalGoals} />

          {showModal && (
            <AddGoalModal
              onClose={() => setShowModal(false)}
              onAddGoal={addGoal}
            />
          )}
        </>
      ) : (
        <div className="guest-message">
          <h2>
            Ласкаво просимо до сервісу <span className="brand">MyGoals</span>!
          </h2>
          <p>
            Цей сервіс допоможе вам планувати, відстежувати та досягати своїх цілей.
            Створюйте завдання, відмічайте виконане та надихайтесь прогресом!
          </p>
          <p className="cta">Щоб почати, зареєструйтесь або увійдіть у свій акаунт:</p>
          <div className="auth-actions">
            <button onClick={() => navigate("/register")} className="auth-button register">
              Зареєструватися
            </button>
            <button onClick={() => navigate("/login")} className="auth-button">
              Увійти
            </button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Goals;
