import React, { useState, useEffect } from "react";
import "./AddGoalModal.css";

const AddGoalModal = ({ onClose, onAddGoal }) => {
  const initialGoalState = {
    title: "",
    description: "",
    imgSrc: "",
    actions: [],
    completed: false
  };

  const [newGoal, setNewGoal] = useState(initialGoalState);
  const [actionInput, setActionInput] = useState("");

  // Очистити форму при відкритті модалки
  useEffect(() => {
    setNewGoal(initialGoalState);
    setActionInput("");
  }, []);

  const addAction = () => {
    if (actionInput.trim()) {
      setNewGoal((prev) => ({
        ...prev,
        actions: [...prev.actions, actionInput.trim()]
      }));
      setActionInput("");
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const localUrl = URL.createObjectURL(file);
      setNewGoal((prev) => ({ ...prev, imgSrc: localUrl }));
    }
  };

  const handleSubmit = () => {
    if (newGoal.title && newGoal.description && newGoal.imgSrc) {
      const goalWithId = {
        ...newGoal,
        id: Date.now()
      };
      onAddGoal(goalWithId);
      setNewGoal(initialGoalState); // Очищаємо форму після збереження
      setActionInput("");
      onClose();
    } else {
      alert("Заповніть всі поля перед збереженням.");
    }
  };

  const handleCancel = () => {
    setNewGoal(initialGoalState); // Очищаємо форму при скасуванні
    setActionInput("");
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>Нова ціль</h2>

        <input
          type="text"
          id="goal-title"
          placeholder="Назва"
          value={newGoal.title}
          onChange={(e) => setNewGoal({ ...newGoal, title: e.target.value })}
        />

        <textarea
          id="goal-description"
          placeholder="Опис"
          value={newGoal.description}
          onChange={(e) => setNewGoal({ ...newGoal, description: e.target.value })}
        ></textarea>

        <label htmlFor="file-upload" className="file-upload">
          <span>Завантажте зображення для вашої цілі</span>
          <input
            type="file"
            id="file-upload"
            accept="image/*"
            onChange={handleImageChange}
          />
        </label>

        {newGoal.imgSrc && (
          <img src={newGoal.imgSrc} alt="preview" className="preview-image" />
        )}

        <div className="actions-section">
          <input
            type="text"
            id="action-input"
            placeholder="Нова дія"
            value={actionInput}
            onChange={(e) => setActionInput(e.target.value)}

          />
          <button className="add-action" onClick={addAction}>Додати дію</button>
          <ul>
            {newGoal.actions.map((action, index) => (
              <li key={index}>{action}</li>
            ))}
          </ul>
        </div>

        <div className="modal-buttons">
          <button className="save" onClick={handleSubmit}>Зберегти</button>
          <button className="cancel" onClick={handleCancel}>Скасувати</button>
        </div>
      </div>
    </div>
  );
};

export default AddGoalModal;
