import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc, query, where } from "firebase/firestore";
import { db } from "./firebase";

// Додати нову ціль користувача
export const addGoalToFirestore = async (userId, goal) => {
  try {
    const goalWithUser = { ...goal, userId };
    const docRef = await addDoc(collection(db, "goals"), goalWithUser);
    return docRef.id; // Використовуємо Firestore автоматичний id
  } catch (e) {
    console.error("Помилка додавання цілі: ", e);
    throw e;
  }
};

// Отримати цілі користувача
export const fetchGoals = async (userId) => {
  try {
    const q = query(collection(db, "goals"), where("userId", "==", userId));
    const querySnapshot = await getDocs(q);
    const goals = [];
    querySnapshot.forEach((doc) => {
      goals.push({ id: doc.id, ...doc.data() }); // Використовуємо doc.id як id цілі
    });
    return goals;
  } catch (e) {
    console.error("Помилка отримання цілей: ", e);
    throw e;
  }
};

// Видалити ціль користувача
export const deleteGoalFromFirestore = async (userId, goalId) => {
  try {
    if (typeof goalId !== 'string') {
      goalId = String(goalId);
    }

    if (typeof goalId !== 'string') {
      throw new Error(`goalId має бути рядком, а не ${typeof goalId}`);
    }

    const goalRef = doc(db, "goals", goalId);
    await deleteDoc(goalRef);
  } catch (e) {
    console.error("Помилка при видаленні цілі: ", e);
    throw e;
  }
};

// Оновити статус виконання цілі
export const toggleGoalStatus = async (userId, updatedGoal) => {
  try {
    const goalRef = doc(db, "goals", updatedGoal.id); // Використовуємо id, отримане з Firestore
    await updateDoc(goalRef, {
      completed: updatedGoal.completed,
    });
  } catch (e) {
    console.error("Помилка оновлення статусу цілі: ", e);
    throw e;
  }
};
