import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth, db } from "../../firebase/firebase";  // імпортуємо Firebase
import { doc, getDoc } from "firebase/firestore";  // імпортуємо функції для роботи з Firestore
import Progress from "../Progress/Progress";
import AddGoalModal from "../AddGoalModal/AddGoalModal";
import "./Goals.css";
import goalsData from "../../json/goalsData.json"; // імпортуємо локальні дані

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [filter, setFilter] = useState("all");
  const [showModal, setShowModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setGoals(goalsData); // Завантажуємо локальні цілі
      } else {
        setIsAuthenticated(false);
      }
    });
    return () => unsubscribe();
  }, []);

  // Функція для отримання опису з Firestore
  const fetchGoalDescription = async (goalId) => {
    try {
      const docRef = doc(db, "goalDescriptions", String(goalId)); // ← Ключове виправлення
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        return docSnap.data().description;
      } else {
        console.log("Опис не знайдено для цілі з ID", goalId);
        return "Опис не знайдено.";
      }
    } catch (error) {
      console.error("Помилка при отриманні опису з Firestore: ", error);
      return "Помилка при отриманні опису.";
    }
  };


  const toggleGoal = (index) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal, i) =>
        i === index ? { ...goal, completed: !goal.completed } : goal
      )
    );
  };

  const deleteGoal = (index) => {
    setGoals((prevGoals) => prevGoals.filter((_, i) => i !== index));
  };

  const addGoal = (newGoal) => {
    setGoals((prevGoals) => [...prevGoals, newGoal]);
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all"
      ? true
      : filter === "completed"
      ? goal.completed
      : !goal.completed
  );

  const completedGoals = goals.filter((goal) => goal.completed).length;
  const totalGoals = goals.length;

  return (
    <section id="goals">
      {isAuthenticated ? (
        <>
          <div className="filter-container">
            <select onChange={(e) => setFilter(e.target.value)} value={filter}>
              <option value="all">Всі</option>
              <option value="active">Активні</option>
              <option value="completed">Виконані</option>
            </select>
          </div>

          <div className="goal-cards">
            {filteredGoals.map((goal, index) => (
              <article
                className={`goal-card ${goal.completed ? "completed" : ""}`}
                key={goal.id}
                onClick={async () => {
                  const description = await fetchGoalDescription(goal.id);  // Отримуємо опис
                  navigate(`/goal/${goal.id}`, { state: { description } });  // Передаємо опис на сторінку деталей
                }}
              >
                <img src={goal.imgSrc} alt={goal.title} />
                <h3>{goal.title}</h3>
                <button
                  className="mark-done"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleGoal(index);
                  }}
                >
                  {goal.completed ? "Виконано" : "Виконати"}
                </button>
                <button
                  className="delete-goal"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteGoal(index);
                  }}
                >
                  ✖
                </button>
              </article>
            ))}

            <article className="goal-card add-goal" onClick={() => setShowModal(true)}>
              <div className="plus-sign">+</div>
              <h3>Додати ціль</h3>
            </article>
          </div>

          <Progress completedGoals={completedGoals} totalGoals={totalGoals} />

          {showModal && (
            <AddGoalModal
              onClose={() => setShowModal(false)}
              onAddGoal={addGoal}
            />
          )}
        </>
      ) : (
        <div className="guest-message">
          <h2>
            Ласкаво просимо до сервісу <span className="brand">MyGoals</span>!
          </h2>
          <p>
            Цей сервіс допоможе вам планувати, відстежувати та досягати своїх цілей.
            Створюйте завдання, відмічайте виконане та надихайтесь прогресом!
          </p>
          <p className="cta">Щоб почати, зареєструйтесь або увійдіть у свій акаунт:</p>
          <div className="auth-actions">
            <button onClick={() => navigate("/register")} className="auth-button register">Зареєструватися</button>
            <button onClick={() => navigate("/login")} className="auth-button">Увійти</button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Goals;
