import React, { useState, useEffect } from "react";
import Progress from "../Progress/Progress";
import Community from "../Community/Community";
import "./Goals.css";
import goalsData from "../../json/goalsData.json";

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [filter, setFilter] = useState("all");
  const [showModal, setShowModal] = useState(false);

  const [newGoal, setNewGoal] = useState({
    title: "",
    description: "",
    imgSrc: "",
    actions: [],
    completed: false
  });

  const [actionInput, setActionInput] = useState("");

  useEffect(() => {
    setGoals(goalsData);
  }, []);

  const toggleGoal = (index) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal, i) =>
        i === index ? { ...goal, completed: !goal.completed } : goal
      )
    );
  };

  const deleteGoal = (index) => {
    setGoals((prevGoals) => prevGoals.filter((_, i) => i !== index));
  };

  const addAction = () => {
    if (actionInput.trim()) {
      setNewGoal((prev) => ({
        ...prev,
        actions: [...prev.actions, actionInput.trim()]
      }));
      setActionInput("");
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const localUrl = URL.createObjectURL(file);
      setNewGoal((prev) => ({ ...prev, imgSrc: localUrl }));
    }
  };

  const handleAddGoal = () => {
    if (newGoal.title && newGoal.description && newGoal.imgSrc) {
      const newGoalWithId = {
        ...newGoal,
        id: Date.now()
      };
      setGoals((prev) => [...prev, newGoalWithId]);
      setNewGoal({
        title: "",
        description: "",
        imgSrc: "",
        actions: [],
        completed: false
      });
      setShowModal(false);
    } else {
      alert("Будь ласка, заповніть всі поля");
    }
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all" ? true : filter === "completed" ? goal.completed : !goal.completed
  );

  const completedGoals = goals.filter((goal) => goal.completed).length;
  const totalGoals = goals.length;

  return (
    <section id="goals">
      <div className="filter-container">
        <select onChange={(e) => setFilter(e.target.value)} value={filter}>
          <option value="all">Всі</option>
          <option value="active">Активні</option>
          <option value="completed">Виконані</option>
        </select>
      </div>

      <div className="goal-cards">
        {filteredGoals.map((goal, index) => (
          <article
            className={`goal-card ${goal.completed ? "completed" : ""}`}
            key={goal.id}
            onClick={() => window.location.href = `/goal/${goal.id}`}
          >
            <img src={goal.imgSrc} alt={goal.title} />
            <h3>{goal.title}</h3>
            <button className="mark-done" onClick={(e) => {
              e.stopPropagation();
              toggleGoal(index);
            }}>
              {goal.completed ? "Виконано" : "Виконати"}
            </button>
            <button className="delete-goal" onClick={(e) => {
              e.stopPropagation();
              deleteGoal(index);
            }}>
              ✖
            </button>
          </article>
        ))}

        <article className="goal-card add-goal" onClick={() => setShowModal(true)}>
          <div className="plus-sign">+</div>
          <h3>Додати ціль</h3>
        </article>
      </div>

      <Progress completedGoals={completedGoals} totalGoals={totalGoals} />
      <Community />

      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Нова ціль</h2>
            <input
              type="text"
              placeholder="Назва"
              value={newGoal.title}
              onChange={(e) => setNewGoal({ ...newGoal, title: e.target.value })}
            />
            <textarea
              placeholder="Опис"
              value={newGoal.description}
              onChange={(e) => setNewGoal({ ...newGoal, description: e.target.value })}
            ></textarea>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            {newGoal.imgSrc && <img src={newGoal.imgSrc} alt="preview" className="preview-image" />}
            <div className="actions-section">
              <input
                type="text"
                placeholder="Нова дія"
                value={actionInput}
                onChange={(e) => setActionInput(e.target.value)}
              />
              <button onClick={addAction}>Додати дію</button>
              <ul>
                {newGoal.actions.map((action, index) => (
                  <li key={index}>✔ {action}</li>
                ))}
              </ul>
            </div>
            <div className="modal-buttons">
              <button onClick={handleAddGoal}>Зберегти</button>
              <button className="cancel" onClick={() => setShowModal(false)}>Скасувати</button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Goals;
