import React, { useState } from "react";
import { Link } from "react-router-dom";
import Progress from "./Progress";
import Community from "./Community";
import "./css/Goals.css";

const initialGoals = [
  { id: 1, imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", completed: false },
  { id: 2, imgSrc: "/img/goal2.webp", title: "Купити новий телефон", completed: false },
  { id: 3, imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", completed: false },
  { id: 4, imgSrc: "/img/goal4.jpg", title: "Прочитати три книги", completed: false },
  { id: 5, imgSrc: "/img/goal5.jpg", title: "Навчитися грати на гітарі", completed: false },
  { id: 6, imgSrc: "/img/goal6.jpg", title: "Купити навушники", completed: false },
  { id: 7, imgSrc: "/img/goal7.png", title: "Здати IELTS", completed: false },
  { id: 8, imgSrc: "/img/goal8.jpg", title: "Купити сережки", completed: false },
  { id: 9, imgSrc: "/img/goal9.webp", title: "Купити духи", completed: false },
  { id: 10, imgSrc: "/img/goal10.webp", title: "Освоїти HTML і CSS", completed: false },
  { id: 11, imgSrc: "/img/goal11.webp", title: "Купити Porsche", completed: false },
  { id: 12, imgSrc: "/img/goal12.jpeg", title: "Купити Dyson", completed: false },
];

const Goals = () => {
  const [goals, setGoals] = useState(initialGoals);
  const [filter, setFilter] = useState("all");

  const toggleGoal = (index) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal, i) =>
        i === index ? { ...goal, completed: !goal.completed } : goal
      )
    );
  };

  const deleteGoal = (index) => {
    setGoals((prevGoals) => prevGoals.filter((_, i) => i !== index));
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all" ? true : filter === "completed" ? goal.completed : !goal.completed
  );

  const completedGoals = goals.filter((goal) => goal.completed).length;
  const totalGoals = goals.length;

  return (
    <section id="goals">
      <div className="filter-container">
        <select onChange={(e) => setFilter(e.target.value)} value={filter}>
          <option value="all">Всі</option>
          <option value="active">Активні</option>
          <option value="completed">Виконані</option>
        </select>
      </div>

      <div className="goal-cards">
        {filteredGoals.map((goal, index) => (
          <article className={`goal-card ${goal.completed ? "completed" : ""}`} key={goal.id}>
            <img src={goal.imgSrc} alt={goal.title} />
            <h3>
              <Link to={`/goal/${goal.id}`}>{goal.title}</Link>
            </h3>
            <button className="mark-done" onClick={() => toggleGoal(index)}>
              {goal.completed ? "Виконано" : "Виконати"}
            </button>
            <button className="delete-goal" onClick={() => deleteGoal(index)}>✖</button>
          </article>
        ))}

        <article className="goal-card add-goal">
          <div className="plus-sign">+</div>
          <h3>Додати ціль</h3>
        </article>
      </div>
      <Progress completedGoals={completedGoals} totalGoals={totalGoals} />
      <Community />
    </section>
  );
};

export default Goals;
