import React, { useState, useEffect } from "react";
import "./css/Goals.css";

const initialGoals = [
  { imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", completed: false },
  { imgSrc: "/img/goal2.webp", title: "Купити новий телефон", completed: false },
  { imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", completed: false },
  { imgSrc: "/img/goal4.jpg", title: "Прочитати три книги", completed: false },
  { imgSrc: "/img/goal5.jpg", title: "Навчитися грати на гітарі", completed: false },
  { imgSrc: "/img/goal6.jpg", title: "Купити навушники", completed: false },
  { imgSrc: "/img/goal7.png", title: "Здати IELTS", completed: false },
  { imgSrc: "/img/goal8.jpg", title: "Купити сережки", completed: false },
  { imgSrc: "/img/goal9.webp", title: "Купити духи", completed: false },
  { imgSrc: "/img/goal10.webp", title: "Освоїти HTML і CSS", completed: false },
  { imgSrc: "/img/goal11.webp", title: "Купити Porsche", completed: false },
  { imgSrc: "/img/goal12.jpeg", title: "Купити Dyson", completed: false },
];

const motivationMessages = [
  "🔥 Ніколи не здавайся – ти ближче, ніж здається!",
  "💪 Кожен день – це ще один крок до мрії!",
  "🚀 Дій сьогодні, а не завтра!",
  "🌟 Маленькі досягнення ведуть до великих перемог!",
  "🎯 Фокусуйся на результаті, а не на труднощах!",
];

const Goals = () => {
  const [goals, setGoals] = useState(initialGoals);
  const [filter, setFilter] = useState("all");
  const [motivation, setMotivation] = useState("");

  useEffect(() => {
    const randomMessage = motivationMessages[Math.floor(Math.random() * motivationMessages.length)];
    setMotivation(randomMessage);
  }, []);

  const toggleGoal = (index) => {
    setGoals((prevGoals) =>
      prevGoals.map((goal, i) =>
        i === index ? { ...goal, completed: !goal.completed } : goal
      )
    );
  };

  const deleteGoal = (index) => {
    setGoals((prevGoals) => prevGoals.filter((_, i) => i !== index));
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all" ? true : filter === "completed" ? goal.completed : !goal.completed
  );

  return (
    <section id="goals">
      <p className="motivation-message">{motivation}</p>

      <div className="filter-container">
        <label>Фільтр:</label>
        <select onChange={(e) => setFilter(e.target.value)} value={filter}>
          <option value="all">Всі</option>
          <option value="active">Активні</option>
          <option value="completed">Виконані</option>
        </select>
      </div>

      <div className="goal-cards">
        {filteredGoals.map((goal, index) => (
          <article className={`goal-card ${goal.completed ? "completed" : ""}`} key={index}>
            <img src={goal.imgSrc} alt={goal.title} />
            <h3>{goal.title}</h3>
            <button className="mark-done" onClick={() => toggleGoal(index)}>
              {goal.completed ? "Виконано" : "Виконати"}
            </button>
            <button className="delete-goal" onClick={() => deleteGoal(index)}>✖</button>
          </article>
        ))}

        <article className="goal-card add-goal">
          <div className="plus-sign">+</div>
          <h3>Додати ціль</h3>
        </article>
      </div>
    </section>
  );
};

export default Goals;
