import React from "react";
import { useParams } from "react-router-dom";
import "./css/GoalDetails.css";

const initialGoals = [  
  { id: 1, imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", description: "Записатися у спортзал і регулярно займатися спортом." },
  { id: 2, imgSrc: "/img/goal2.webp", title: "Купити новий телефон", description: "Оновити свій смартфон до нової моделі." },
  { id: 3, imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", description: "Запланувати подорож до Риму, Флоренції або Венеції." },
];

const GoalDetails = () => {
  const { id } = useParams(); // Отримуємо id цілі з URL
  const goal = initialGoals.find((g) => g.id === parseInt(id)); // Знаходимо ціль за id

  if (!goal) {
    return <h2>Ціль не знайдено!</h2>;
  }

  return (
    <section id="goal-details">
      <img src={goal.imgSrc} alt={goal.title} />
      <h2>{goal.title}</h2>
      <p>{goal.description}</p>
    </section>
  );
};

export default GoalDetails;
