// src/firebase/filestore.js

const API_URL = "http/api/goals"; // Працює через proxy або повна адреса

export const getUserGoals = async (userId) => {
  const res = await fetch(`${API_URL}?userId=${userId}`);
  if (!res.ok) throw new Error("Не вдалося отримати цілі");
  return await res.json();
};

export const createGoal = async (goal) => {
  const res = await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(goal),
  });
  if (!res.ok) throw new Error("Не вдалося створити ціль");
  return await res.json();
};

export const updateGoal = async (goalId, updates) => {
  const res = await fetch(`${API_URL}/${goalId}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(updates),
  });
  if (!res.ok) throw new Error("Не вдалося оновити ціль");
};

export const deleteGoal = async (goalId) => {
  const res = await fetch(`${API_URL}/${goalId}`, {
    method: "DELETE",
  });
  if (!res.ok) throw new Error("Не вдалося видалити ціль");
};
