import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Progress from "../Progress/Progress";
import AddGoalModal from "../AddGoalModal/AddGoalModal";
import "./Goals.css";
import { auth } from "../../firebase/firebase"; // Імпортуємо auth з Firebase
import { onAuthStateChanged } from "firebase/auth"; // Для перевірки стану авторизації
import { fetchGoals, addGoalToFirestore, deleteGoalFromFirestore, toggleGoalStatus } from "../../firebase/firestore"; // Імпорт firestore функцій

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [filter, setFilter] = useState("all");
  const [showModal, setShowModal] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Стан авторизації
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      if (user) {
        setIsAuthenticated(true);
        setUserId(user.uid);
        const userGoals = await fetchGoals(user.uid);
        setGoals(userGoals);
      } else {
        setIsAuthenticated(false);
        navigate("/login");
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  const toggleGoal = async (goal) => {
    const updatedGoal = { ...goal, completed: !goal.completed };
    await toggleGoalStatus(userId, updatedGoal);
    const updatedGoals = await fetchGoals(userId);
    setGoals(updatedGoals);
  };

  const deleteGoal = async (goalId) => {
    await deleteGoalFromFirestore(userId, goalId);
    const updatedGoals = await fetchGoals(userId);
    setGoals(updatedGoals);
  };

  const addGoal = async (newGoal) => {
    await addGoalToFirestore(userId, newGoal);
    const updatedGoals = await fetchGoals(userId);
    setGoals(updatedGoals);
  };

  const filteredGoals = goals.filter((goal) =>
    filter === "all"
      ? true
      : filter === "completed"
      ? goal.completed
      : !goal.completed
  );

  const completedGoals = goals.filter((goal) => goal.completed).length;
  const totalGoals = goals.length;

  return (
    <section id="goals">
      {isAuthenticated ? (
        <>
          <div className="filter-container">
            <select onChange={(e) => setFilter(e.target.value)} value={filter}>
              <option value="all">Всі</option>
              <option value="active">Активні</option>
              <option value="completed">Виконані</option>
            </select>
          </div>

          <div className="goal-cards">
            {filteredGoals.map((goal) => (
              <article
                className={`goal-card ${goal.completed ? "completed" : ""}`}
                key={goal.id}
                onClick={() => navigate(`/goal/${goal.id}`)}
              >
                <img src={goal.imgSrc} alt={goal.title} />
                <h3>{goal.title}</h3>
                <button
                  className="mark-done"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleGoal(goal);
                  }}
                >
                  {goal.completed ? "Виконано" : "Виконати"}
                </button>
                <button
                  className="delete-goal"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteGoal(goal.id);
                  }}
                >
                  ✖
                </button>
              </article>
            ))}

            <article className="goal-card add-goal" onClick={() => setShowModal(true)}>
              <div className="plus-sign">+</div>
              <h3>Додати ціль</h3>
            </article>
          </div>

          <Progress completedGoals={completedGoals} totalGoals={totalGoals} />

          {showModal && (
            <AddGoalModal
              onClose={() => setShowModal(false)}
              onAddGoal={addGoal}
            />
          )}
        </>
      ) : (
        <div className="guest-message">
          <h2>
            Ласкаво просимо до сервісу <span className="brand">MyGoals</span>!
          </h2>
          <p>
            Цей сервіс допоможе вам планувати, відстежувати та досягати своїх особистих і професійних цілей.
            Створюйте завдання, відмічайте виконане та надихайтесь прогресом!
          </p>
          <p className="cta">Щоб почати, зареєструйтесь або увійдіть у свій акаунт:</p>
          <div className="auth-actions">
            <button onClick={() => navigate("/register")} className="auth-button register">Зареєструватися</button>
            <button onClick={() => navigate("/login")} className="auth-button">Увійти</button>
          </div>
        </div>
      )}
    </section>
  );
};

export default Goals;
