import React from "react";
import { useLocation, Link } from "react-router-dom";
import "./GoalDetails.css";

const GoalDetails = () => {
  const { state } = useLocation();
  const description = state?.description || "Опис не знайдено";
  const imgSrc = state?.imgSrc || ""; 
  const title = state?.title || "Немає назви";
  const actions = state?.actions || [];

  return (
    <div className="goals-details-container">
      <div className="goals-cards">
        <h2>{title}</h2> {/* Виводимо заголовок */}
        {imgSrc && <img src={imgSrc} alt="Зображення цілі" className="goal-image" />} {/* Виводимо зображення */}
        <p className="goals-description">{description}</p> {/* Виводимо опис */}

        <div className="goal-actions">
          <h3>Дії:</h3>
          <ul>
            {actions.length > 0 ? (
              actions.map((action, index) => (
                <li key={index}>{action}</li> // Виводимо кожну дію
              ))
            ) : (
              <li>Дії не задані.</li>
            )}
          </ul>
        </div>

        <Link to="/" className="back-button">Назад до цілей</Link>
      </div>
    </div>
  );
};

export default GoalDetails;
