import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import "./css/GoalDetails.css";
import goalsData from './path_to_goals.json';


const initialGoals = [
  { id: 1, imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", description: "Регулярні тренування допомагають підтримувати здоров'я та покращують фізичну форму. Запишися у спортзал і склади програму тренувань!" },
  { id: 2, imgSrc: "/img/goal2.webp", title: "Купити новий телефон", description: "Оновлення смартфона може покращити якість фото, швидкість роботи та загальне задоволення від використання." },
  { id: 3, imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", description: "Італія – це неймовірна кухня, багата культура та вражаюча архітектура. Сплануй подорож до Риму, Венеції чи Флоренції!" },
  { id: 4, imgSrc: "/img/goal4.jpg", title: "Прочитати три книги", description: "Читання розширює світогляд і покращує пам’ять. Обери три книги, які давно хотів прочитати!" },
  { id: 5, imgSrc: "/img/goal5.jpg", title: "Навчитися грати на гітарі", description: "Гра на гітарі – чудовий спосіб виразити себе та розслабитися. Почни з простих акордів і улюблених пісень!" },
  { id: 6, imgSrc: "/img/goal6.jpg", title: "Купити навушники", description: "Якісні навушники дозволять насолоджуватися музикою та підвищити продуктивність під час роботи або навчання." },
  { id: 7, imgSrc: "/img/goal7.png", title: "Здати IELTS", description: "Складання IELTS відкриває двері до навчання за кордоном та міжнародних можливостей. Почни підготовку сьогодні!" },
  { id: 8, imgSrc: "/img/goal8.jpg", title: "Купити сережки", description: "Обери стильні сережки, які підкреслять твою унікальність і стануть чудовим доповненням до образу." },
  { id: 9, imgSrc: "/img/goal9.webp", title: "Купити духи", description: "Аромат – це твій підпис. Знайди парфуми, які ідеально відображають твій стиль і настрій!" },
  { id: 10, imgSrc: "/img/goal10.webp", title: "Освоїти HTML і CSS", description: "Основи веб-розробки допоможуть створювати сайти та відкриють двері у світ IT!" },
  { id: 11, imgSrc: "/img/goal11.webp", title: "Купити Porsche", description: "Мрія про спортивний автомобіль може стати реальністю. Склади фінансовий план і рухайся до мети!" },
  { id: 12, imgSrc: "/img/goal12.jpeg", title: "Купити Dyson", description: "Сучасний пилосос Dyson – це не лише ефективне прибирання, а й стильний гаджет для дому!" },
];

const GoalDetails = () => {
  const { id } = useParams();
  const goal = initialGoals.find((g) => g.id === parseInt(id));

  if (!goal) {
    return <h2 className="error">Ціль не знайдена</h2>;
  }

  return (
    <div className="goal-details-container">
      <div className="goal-card">
        <img src={goal.imgSrc} alt={goal.title} className="goal-image" />
        <h2 className="goal-title">{goal.title}</h2>
        <p className="goal-description">{goal.description}</p>
        <Link to="/goals" className="back-button">Назад до цілей</Link>
      </div>
    </div>
  );
};

export default GoalDetails;
