import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc, query, where } from "firebase/firestore";
import { db } from "./firebase";
import { v4 as uuidv4 } from 'uuid';  // Імпортуємо uuid для створення унікального id

// Додати нову ціль користувача
export const addGoalToFirestore = async (userId, goal) => {
  try {
    const goalWithId = { ...goal, id: uuidv4(), userId };  // Генерація унікального id за допомогою uuid
    const docRef = await addDoc(collection(db, "goals"), goalWithId);
    console.log("Ціль додана з id: ", docRef.id);  // docRef.id є id документа, згенерованого Firebase
    return docRef.id;  // Повертаємо id для подальшого використання
  } catch (e) {
    console.error("Помилка додавання цілі: ", e);
    throw e;
  }
};

// Отримати цілі користувача
export const fetchGoals = async (userId) => {
  try {
    const q = query(collection(db, "goals"), where("userId", "==", userId));
    const querySnapshot = await getDocs(q);
    const goals = [];
    querySnapshot.forEach((doc) => {
      goals.push({ id: doc.id, ...doc.data() });
    });
    return goals;
  } catch (e) {
    console.error("Помилка отримання цілей: ", e);
    throw e;
  }
};

// Видалити ціль користувача
export const deleteGoalFromFirestore = async (userId, goalId) => {
  try {
    // Переконаємося, що goalId це рядок
    if (typeof goalId !== 'string') {
      console.log("goalId тип:", typeof goalId);
      goalId = String(goalId); // перетворюємо в рядок, якщо це число
    }

    // Перевірка на правильність типу
    if (typeof goalId !== 'string') {
      throw new Error(`goalId має бути рядком, а не ${typeof goalId}`);
    }

    // Логування для перевірки goalId
    console.log("Спроба видалити ціль з goalId:", goalId);

    // Формуємо правильне посилання на документ
    const goalRef = doc(db, "goals", goalId);

    // Видаляємо документ
    await deleteDoc(goalRef);
    console.log(`Ціль з goalId ${goalId} була успішно видалена!`);
  } catch (e) {
    console.error("Помилка при видаленні цілі: ", e);
    throw e;
  }
};

// Оновити статус виконання цілі
export const toggleGoalStatus = async (userId, updatedGoal) => {
  try {
    const goalRef = doc(db, "goals", updatedGoal.id);
    await updateDoc(goalRef, {
      completed: updatedGoal.completed,
    });
    console.log(`Статус цілі з id ${updatedGoal.id} оновлений на ${updatedGoal.completed}`);
  } catch (e) {
    console.error("Помилка оновлення статусу цілі: ", e);
    throw e;
  }
};
