import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const initialGoals = [
  { id: 1, imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", description: "Записатися у спортзал і регулярно займатися спортом." },
  { id: 2, imgSrc: "/img/goal2.webp", title: "Купити новий телефон", description: "Оновити смартфон до нової моделі з кращими характеристиками." },
  { id: 3, imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", description: "Запланувати подорож до Риму, Флоренції або Венеції." },
  { id: 4, imgSrc: "/img/goal4.jpg", title: "Прочитати три книги", description: "Обрати три цікаві книги та дочитати їх до кінця." },
  { id: 5, imgSrc: "/img/goal5.jpg", title: "Навчитися грати на гітарі", description: "Пройти курс гри на гітарі та навчитись грати улюблені пісні." },
  { id: 6, imgSrc: "/img/goal6.jpg", title: "Купити навушники", description: "Придбати якісні навушники для музики та роботи." },
  { id: 7, imgSrc: "/img/goal7.png", title: "Здати IELTS", description: "Підготуватись до тесту IELTS та отримати високий бал." },
  { id: 8, imgSrc: "/img/goal8.jpg", title: "Купити сережки", description: "Обрати красиві сережки для повсякденного носіння." },
  { id: 9, imgSrc: "/img/goal9.webp", title: "Купити духи", description: "Знайти парфуми з ідеальним ароматом." },
  { id: 10, imgSrc: "/img/goal10.webp", title: "Освоїти HTML і CSS", description: "Вивчити основи веб-розробки та створити перший сайт." },
  { id: 11, imgSrc: "/img/goal11.webp", title: "Купити Porsche", description: "Зібрати кошти та придбати спортивний автомобіль." },
  { id: 12, imgSrc: "/img/goal12.jpeg", title: "Купити Dyson", description: "Придбати сучасний пилосос Dyson для дому." },
];

const GoalDetails = () => {
  const { id } = useParams();
  const goal = initialGoals.find((g) => g.id === parseInt(id));

  if (!goal) {
    return <h2>Ціль не знайдена</h2>;
  }

  return (
    <div className="goal-details">
      <img src={goal.imgSrc} alt={goal.title} />
      <h2>{goal.title}</h2>
      <p>{goal.description}</p>
      <Link to="/">Назад</Link>
    </div>
  );
};

export default GoalDetails;
