import React, { useState } from 'react';
import './Header.css'; // Стилі для Header

function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Функція для переключення бургер-меню
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header>
      <nav>
        <div className="logo-container">
          <img src="public/img/logo.png" alt="Логотип" className="logo" />
        </div>
        <button className="burger-menu" onClick={toggleMenu}>
          &#9776;
        </button>
        <ul className={`nav-list ${isMenuOpen ? 'show' : ''}`}>
          <li><a href="#goals">Мої цілі</a></li>
          <li><a href="#progress">Прогрес</a></li>
          <li><a href="#community">Спільнота</a></li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
