import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import "./css/GoalDetails.css";
import goalsData from './public/json/jsonDetails.json'; // правильний шлях до JSON файлу

const GoalDetails = () => {
  const { id } = useParams();
  const [goal, setGoal] = useState(null);

  useEffect(() => {
    // Знайти ціль за id із goalsData
    const foundGoal = goalsData.find((g) => g.id === parseInt(id));
    setGoal(foundGoal);
  }, [id]); // Перезапускати пошук, коли змінюється id

  if (!goal) {
    return <h2 className="error">Ціль не знайдена</h2>;
  }

  return (
    <div className="goal-details-container">
      <div className="goal-card">
        <img src={goal.imgSrc} alt={goal.title} className="goal-image" />
        <h2 className="goal-title">{goal.title}</h2>
        <p className="goal-description">{goal.description}</p>
        <p className="goal-deadline">Дедлайн: {goal.deadline}</p>
        <Link to="/goals" className="back-button">Назад до цілей</Link>
      </div>
    </div>
  );
};

export default GoalDetails;
