import React, { useState } from "react";
import "./css/Goals.css";
import Progress from "./Progress";
import Community from "./Community";
import { Link } from "react-router-dom";

const initialGoals = [  
  { imgSrc: "/img/goal1.jpg", title: "Піти в спортзал", completed: false },
  { imgSrc: "/img/goal2.webp", title: "Купити новий телефон", completed: false },
  { imgSrc: "/img/goal3.jpg", title: "Поїхати в Італію", completed: false },
  { imgSrc: "/img/goal4.jpg", title: "Прочитати три книги", completed: false },
  { imgSrc: "/img/goal5.jpg", title: "Навчитися грати на гітарі", completed: false },
  { imgSrc: "/img/goal6.jpg", title: "Купити навушники", completed: false },
  { imgSrc: "/img/goal7.png", title: "Здати IELTS", completed: false },
  { imgSrc: "/img/goal8.jpg", title: "Купити сережки", completed: false },
  { imgSrc: "/img/goal9.webp", title: "Купити духи", completed: false },
  { imgSrc: "/img/goal10.webp", title: "Освоїти HTML і CSS", completed: false },
  { imgSrc: "/img/goal11.webp", title: "Купити Porsche", completed: false },
  { imgSrc: "/img/goal12.jpeg", title: "Купити Dyson", completed: false },
];

const Goals = () => {
  const [goals, setGoals] = useState(initialGoals);
  const [filter, setFilter] = useState("all");

  const filteredGoals = goals.filter((goal) =>
    filter === "all" ? true : filter === "completed" ? goal.completed : !goal.completed
  );

  return (
    <section id="goals">
      <div className="filter-container">
        <select onChange={(e) => setFilter(e.target.value)} value={filter}>
          <option value="all">Всі</option>
          <option value="active">Активні</option>
          <option value="completed">Виконані</option>
        </select>
      </div>

      <div className="goal-cards">
        {filteredGoals.map((goal) => (
          <article className={`goal-card ${goal.completed ? "completed" : ""}`} key={goal.id}>
            <img src={goal.imgSrc} alt={goal.title} />
            <h3>{goal.title}</h3>
            <Link to={`/goal/${goal.id}`} className="details-link">Детальніше</Link>
          </article>
        ))}
      </div>
    </section>
  );
};

export default Goals;
